/*-----------------------------------------------*
  Styling Options
/*-----------------------------------------------*/
function th_load_css() {
	global $teba_options;
	// Custom colors ( Stylesheet select )
	$th_theme_stylesheet =& $teba_options['th_select_stylesheet'];
	$select_stylesheet = get_post_meta(get_the_ID(),'tb_select_stylesheet',true);
	if($select_stylesheet) {
		$th_theme_stylesheet = $select_stylesheet;
	}
	if ( $th_theme_stylesheet != '' && $th_theme_stylesheet !== 'default.css'){
		wp_enqueue_style('teba-color-theme', TEBA_URI_PATH . '/assets/css/colors/'.$th_theme_stylesheet);
	}
}
add_action('wp_enqueue_scripts', 'th_load_css');
/*-----------------------------------------------*
  Style Inline
/*-----------------------------------------------*/
function teba_add_style_inline() {
	global $teba_options;
    $custom_style = null;
	/* Body background */
    $tb_background_color =& $teba_options['body_background']['background-color']; 
    $tb_background_image =& $teba_options['body_background']['background-image'];
    $tb_background_repeat =& $teba_options['body_background']['background-repeat'];
    $tb_background_position =& $teba_options['body_background']['background-position'];
    $tb_background_size =& $teba_options['body_background']['background-size'];
    $tb_background_attachment =& $teba_options['body_background']['background-attachment'];
	$custom_style .= "body{ background-color: $tb_background_color }";
	if($tb_background_image){
		$custom_style .= "body{ background: url('$tb_background_image') $tb_background_repeat $tb_background_attachment $tb_background_position; background-size: $tb_background_size;}";
